#include <cstdio>
#include <cstdlib>
#include <cstring>
#include <cmath>
#include <algorithm>
#include <vector>
#include <queue>

#define MAXN 100001

typedef long long ll;

using namespace std;

int n;
ll Fibonacci[MAXN] = {0};
bool ans;

void init()
{
    Fibonacci[0] = 1;
    Fibonacci[1] = 1;
    for (int i = 2; i < MAXN; i++)
        Fibonacci[i] = Fibonacci[i - 1] + Fibonacci[i - 2];
}

int main()
{
    init();
    while (~scanf("%d", &n) && n)
    {
        ans = false;
        for (int i = 0; i < MAXN; i++)
            if (Fibonacci[i] == n)
            {
                ans = true;
                break;
            }

        puts(ans ? "Second win" : "First win");
    }
    return 0;
}
