        for (int i = 0; i < m; i++)
        {
            scanf("%d %d %d", &tmp_start, &tmp_end, &tmp_heavy);
            Edge[tmp_start].push_back(make_pair(tmp_end, tmp_heavy));
            Edge[tmp_end].push_back(make_pair(tmp_start, tmp_heavy));
        }
        Djistra();
        printf("Scenario #%d:\n%d\n\n", current + 1, dis[n]); //输出答案
    }
    return 0;
}
